// Obtener todos los gastos del usuario
const allExpenses = $input.all()[0].json.complete_history.all_expenses || [];

// Obtener el mes anterior al actual
const now = new Date();
const prevMonth = new Date(now.getFullYear(), now.getMonth() - 1, 1);
const prevMonthNum = prevMonth.getMonth() + 1; // 1-12
const prevYear = prevMonth.getFullYear();

// Filtrar gastos del mes anterior
const filteredExpenses = allExpenses.filter(expense => {
  const date = new Date(expense.date);
  return (date.getMonth() + 1 === prevMonthNum) && (date.getFullYear() === prevYear);
});

// Calcular totales del mes anterior
const totalSpent = filteredExpenses.reduce((sum, expense) => sum + parseFloat(expense.amount || 0), 0);

const monthlyBudget = parseFloat($input.all()[0].json.budget.monthly_limit || 0);
const percentageUsed = monthlyBudget > 0 ? (totalSpent / monthlyBudget * 100).toFixed(2) : 0;

const monthToAnalyze = prevMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });

// Agrupar gastos por categor√≠a
const categories = {};
filteredExpenses.forEach(expense => {
  const category = expense.category?.name || 'Uncategorized';
  if (!categories[category]) {
    categories[category] = 0;
  }
  categories[category] += parseFloat(expense.amount || 0);
});

// Preparar datos para OpenAI
const dataForAI = {
  user: $input.all()[0].json.username,
  monthly_budget: monthlyBudget,
  analyzed_month: monthToAnalyze,
  previous_month_expenses: filteredExpenses,
  total_spent: totalSpent,
  percentage_used: percentageUsed,
  remaining_money: monthlyBudget - totalSpent,
  number_of_expenses: filteredExpenses.length,
  categories_summary: categories,
  has_data: filteredExpenses.length > 0
};

// Retornar en formato correcto para n8n
return [
  {
    json: {
      ...dataForAI,
      email: $input.all()[0].json.email,
      username: $input.all()[0].json.username,
      budget: $input.all()[0].json.budget,
      complete_history: $input.all()[0].json.complete_history
    }
  }
];

